mapper.insert:
INSERT INTO auth_factor (
    user_id, factor_type, factor_value, factor_code, expire_time, status, is_default, create_time, update_time
) VALUES (
    #{userId}, #{factorType}, #{factorValue}, #{factorCode}, #{expireTime}, #{status}, #{isDefault}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT 
FROM auth_factor
WHERE user_id = #{userId} 
  AND factor_type = 'sms'
  AND factor_value = #{phone}
  AND factor_code = #{code}
  AND status = 1
LIMIT 1

SELECT 
FROM auth_factor
WHERE user_id = #{userId} 
  AND factor_type = 'email'
  AND factor_value = #{email}
  AND factor_code = #{code}
  AND status = 1
LIMIT 1
