mapper.select:
SELECT 
FROM temp_permission
WHERE user_id = #{userId} 
  AND start_time <= #{currentTime}
  AND end_time >= #{currentTime}
  AND status = 1
ORDER BY create_time DESC

mapper.insert:
INSERT INTO temp_permission (
    user_id, role_id, permission_id, reason, start_time, end_time, status, grant_by, grant_reason, create_time, update_time
) VALUES (
    #{userId}, #{roleId}, #{permissionId}, #{reason}, #{startTime}, #{endTime}, #{status}, #{grantBy}, #{grantReason}, #{createTime}, #{updateTime}
)
