mapper.select:
SELECT * FROM experiment_project
WHERE 1=1

    AND category = #{category}


    AND level = #{level}


    AND (name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%'))

mapper.insert:
INSERT INTO experiment_project ( name, category, description, principle, purpose, method, steps, level, image_url, video_url, collaboration_type, created_at, updated_at, created_by, screenshot, scene_data, experiment_params, scene_json_path ) VALUES ( #{name}, #{category}, #{description}, #{principle}, #{purpose}, #{method}, #{steps}, #{level}, #{imageUrl}, #{videoUrl}, #{projectType}, NOW(), NOW(), #{createdBy}, #{screenshot},
#{sceneData}
null , #{experimentParams}, #{sceneJsonPath} )

mapper.select:
SELECT * FROM experiment_project WHERE 1=1
AND category = #{category}
ORDER BY created_at DESC
ORDER BY views DESC
ORDER BY id DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM experiment_project
WHERE 1=1

    AND category = #{category}

mapper.update:
UPDATE student_project_progress
SET status = 'in_progress'
WHERE project_id = #{projectId}
  AND student_id = #{studentId}
  AND EXISTS (
    SELECT 1 FROM experiment_project
    WHERE id = #{projectId}
      AND created_by != 'admin'
)

UPDATE student_project_progress
SET status = 'completed'
WHERE project_id = #{projectId}
  AND student_id = #{studentId}
  AND EXISTS (
    SELECT 1 FROM experiment_project
    WHERE id = #{projectId}
      AND created_by != 'admin'

mapper.select:
SELECT p.*, COALESCE(l.like_count, 0) AS like_count, COALESCE(f.favorite_count, 0) AS favorite_count, COALESCE(l.like_count, 0) + COALESCE(f.favorite_count, 0) AS hot_count FROM experiment_project p LEFT JOIN ( SELECT resource_id, COUNT(*) AS like_count FROM resource_like GROUP BY resource_id ) l ON p.id = l.resource_id LEFT JOIN ( SELECT resource_id, COUNT(*) AS favorite_count FROM resource_favorite GROUP BY resource_id ) f ON p.id = f.resource_id WHERE p.created_by IN
#{username}
AND p.category = #{category}
AND p.level = #{level}
AND (p.name LIKE CONCAT('%', #{keyword}, '%') OR p.description LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY
hot_count DESC
p.updated_at DESC
like_count DESC
favorite_count DESC
p.updated_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM experiment_project p
WHERE p.created_by IN

    #{username}


    AND p.category = #{category}


    AND p.level = #{level}


    AND (p.name LIKE CONCAT('%', #{keyword}, '%') OR p.description LIKE CONCAT('%', #{keyword}, '%'))
