mapper.select:
SELECT * FROM operation_log
ORDER BY create_time DESC
LIMIT #{limit}

SELECT * FROM operation_log
WHERE user_id = #{userId}
ORDER BY create_time DESC

SELECT * FROM operation_log
WHERE id = #{id}
LIMIT 1

mapper.insert:
INSERT INTO operation_log (
    username, user_id, operation, description, status, user_agent, create_time
) VALUES (
             #{username}, #{userId}, #{operation}, #{description}, #{type}, #{status}, #{ip}, #{userAgent}, NOW()
         )
