mapper.select:
SELECT id, user_id as userId, resource_id as resourceId, created_at as createTime
FROM resource_like
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT #{offset}, #{size}

mapper.update:
UPDATE resource_like
SET user_id = #{userId}, resource_id = #{resourceId}, created_at = #{createTime}
WHERE id = #{id}
