mapper.select:
SELECT 
FROM resource_like
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM resource_like
WHERE user_id = #{userId}

mapper.update:
UPDATE resource_like
SET user_id = #{userId},
    resource_id = #{resourceId},
    created_at = #{createTime}
WHERE id = #{id}

mapper.select:
SELECT 
FROM resource_like
WHERE user_id = #{userId} AND resource_id = #{resourceId}

SELECT COUNT(*)
FROM resource_like
WHERE user_id = #{userId} AND resource_id = #{resourceId}

SELECT COUNT(*)
FROM resource_like
WHERE resource_id = #{resourceId}
