mapper.select:
SELECT 
FROM user
WHERE id = #{id}

SELECT 
FROM user
WHERE username = #{username}

SELECT 
FROM user
WHERE phone = #{phone}

SELECT 
FROM user
WHERE email = #{email}

SELECT 
FROM user
WHERE student_id = #{studentId}

SELECT FROM user
AND username LIKE CONCAT('%', #{username}, '%')
AND real_name LIKE CONCAT('%', #{realName}, '%')
AND department = #{department}
AND status = #{status}
ORDER BY create_time DESC

mapper.insert:
INSERT INTO user (
    username, password, phone, email, real_name, student_id, department, major, status, login_attempts, last_login_time, last_login_ip, lock_time, create_time, update_time
) VALUES (
    #{username}, #{password}, #{phone}, #{email}, #{realName}, #{studentId}, #{department}, #{major}, #{status}, #{loginAttempts}, #{lastLoginTime}, #{lastLoginIp}, #{lockTime}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE user
username = #{username}, password = #{password}, phone = #{phone}, email = #{email}, real_name = #{realName}, student_id = #{studentId}, department = #{department}, major = #{major}, status = #{status}, login_attempts = #{loginAttempts}, last_login_time = #{lastLoginTime}, last_login_ip = #{lastLoginIp}, lock_time = #{lockTime}, update_time = #{updateTime},
WHERE id = #{id}

UPDATE user
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

UPDATE user
SET status = 2, lock_time = #{lockTime}, update_time = NOW()
WHERE id = #{id}

UPDATE user
SET status = 1, lock_time = NULL, update_time = NOW()
WHERE id = #{id}

UPDATE user
SET login_attempts = 0, update_time = NOW()
WHERE id = #{id}

UPDATE user
SET fingerprint = #{fingerprint}, update_time = NOW()
WHERE id = #{id}

